INSERT INTO `cache_field` VALUES ('field:node:1','a:1:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:230:\"I need to quickly and effectively document tips and tricks I come across during the countless hours of problem solving. Some of the information may already exist elsewhere on the web but this site serves as a handy archive for me.\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:13:\"filtered_html\";s:10:\"safe_value\";s:238:\"<p>I need to quickly and effectively document tips and tricks I come across during the countless hours of problem solving. Some of the information may already exist elsewhere on the web but this site serves as a handy archive for me.</p>\n\";s:12:\"safe_summary\";s:0:\"\";}}}}',0,1348259222,1),('field:node:10','a:3:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:546:\"<p>\r\nPHP closure allow a function to enclose a variable and retain a reference to that variable even after the variable falls out of scope.\r\n</p>\r\n<pre class=\"brush:php;\">\r\n&lt;?php\r\n\r\nfunction getClosure()\r\n{\r\n    $localVar = 1;\r\n    \r\n    return function () use ( &amp;$localVar )\r\n    {\r\n        return ++$localVar;\r\n    };\r\n    \r\n}\r\n\r\n\r\n$myClosure = getClosure();\r\necho $myClosure() . PHP_EOL; // prints: 2\r\n\r\n\r\n$notMyClosure = getClosure();\r\necho $notMyClosure() . PHP_EOL; // prints: 2\r\n\r\n\r\necho $myClosure() . PHP_EOL; // prints: 3\r\n</pre>\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:519:\"<p>\nPHP closure allow a function to enclose a variable and retain a reference to that variable even after the variable falls out of scope.\n</p>\n<pre class=\"brush:php;\">\n&lt;?php\n\nfunction getClosure()\n{\n    $localVar = 1;\n    \n    return function () use ( &amp;$localVar )\n    {\n        return ++$localVar;\n    };\n    \n}\n\n\n$myClosure = getClosure();\necho $myClosure() . PHP_EOL; // prints: 2\n\n\n$notMyClosure = getClosure();\necho $notMyClosure() . PHP_EOL; // prints: 2\n\n\necho $myClosure() . PHP_EOL; // prints: 3\n</pre>\";s:12:\"safe_summary\";s:0:\"\";}}}s:10:\"field_tags\";a:1:{s:3:\"und\";a:2:{i:0;a:1:{s:3:\"tid\";s:2:\"12\";}i:1;a:1:{s:3:\"tid\";s:2:\"14\";}}}s:11:\"field_image\";a:0:{}}',0,1349887161,1),('field:node:11','a:3:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:5675:\"<p>This validator class makes a form element \"required\" if and only if another element is set.</p>\r\n\r\n<pre class=\"brush:php\">\r\n&lt;?php\r\n\r\n/**\r\n * Requires an element if conditional elemet is set\r\n *\r\n * @author rmuktader\r\n */\r\nclass My_Validate_RequiredIfConditionalElementSet extends Zend_Validate_NotEmpty\r\n{\r\n     /**\r\n     * Error codes\r\n     * @const string\r\n     */\r\n    const REQUIRED          = \'required\';\r\n    \r\n    const MISSING_COND_ELEM = \'missingCondElem\';\r\n    \r\n    const CONTEXT_NULL      = \'contextNull\';\r\n    \r\n    const CONTEXT_NOT_ARRAY = \'contextNotArray\';\r\n    \r\n    const COND_ELEM_NOT_IN_CONTEXT = \'condElemNotInContext\';\r\n    \r\n\r\n    /**\r\n     * Error messages\r\n     * @var array\r\n     */\r\n    protected $_messageTemplates = array(\r\n        self::REQUIRED          =&gt; \'Value is required and can\\\'t be empty\',\r\n        self::MISSING_COND_ELEM =&gt; \'No conditional element provided\',\r\n        self::CONTEXT_NULL      =&gt; \'The context is null\',\r\n        self::CONTEXT_NOT_ARRAY =&gt; \'The context is not an array\',\r\n        self::COND_ELEM_NOT_IN_CONTEXT  =&gt; \'The conditional element was not found in the context array\',\r\n    );\r\n    \r\n    protected $condElem = null;\r\n    protected $condElemVal = null;\r\n    \r\n    public function __construct($options)\r\n    {\r\n        if (isset($options[\'conditionalElement\']) &amp;&amp;\r\n           !empty($options[\'conditionalElement\'])) {\r\n            \r\n               $this-&gt;setCondElem($options[\'conditionalElement\']);\r\n        } \r\n       \r\n       \r\n       if (isset($options[\'conditionalElementValue\'])) {\r\n\r\n            $this-&gt;setCondElemVal($options[\'conditionalElementValue\']);\r\n           \r\n        } \r\n       \r\n    }\r\n    \r\n    /**\r\n     * Retrieve conditional element\r\n     *\r\n     * @return string\r\n     */\r\n    public function getCondElem() {\r\n        return $this-&gt;condElem;\r\n    }\r\n\r\n    /**\r\n     * @param  string $condElem\r\n     * @return My_Validate_RequiredIfConditionalElementSet\r\n     */\r\n    public function setCondElem($condElem) {\r\n        $this-&gt;condElem = (string) $condElem;\r\n        $this-&gt;condElem = trim($this-&gt;condElem);\r\n        return $this;\r\n    }\r\n\r\n    /**\r\n     * Retrieve conditional element value\r\n     *\r\n     * @return string\r\n     */\r\n    public function getCondElemVal()\r\n    {\r\n        return $this-&gt;condElemVal;\r\n    }\r\n    \r\n    /**\r\n     * @param  string $condElemVal\r\n     * @return My_Validate_RequiredIfConditionalElementSet\r\n     */\r\n    public function setCondElemVal($condElemVal)\r\n    {\r\n        $this-&gt;condElemVal = $condElemVal;\r\n        return $this;\r\n    }\r\n    \r\n    /**\r\n     * Defined by Zend_Validate_Interface\r\n     *\r\n     * @param  string $value\r\n     * @param array $context.  When used with a form this array contains the names of the form elements \r\n     * as keys and the corresponding form element values as the array values.\r\n     *\r\n     * @return boolean\r\n     */\r\n    public function isValid($value, $context = null)\r\n    {\r\n        $condElem = $this-&gt;getCondElem();\r\n        $condElemVal = $this-&gt;getCondElemVal();\r\n     \r\n        if ($condElem === null) {\r\n            $this-&gt;_error(self::MISSING_COND_ELEM);\r\n            return false;\r\n        }\r\n        \r\n        if ($context === null) {\r\n            $this-&gt;_error(self::CONTEXT_NULL);\r\n            return false;\r\n        }\r\n        \r\n        if (!is_array($context)) {\r\n            $this-&gt;_error(self::CONTEXT_NOT_ARRAY);\r\n            return false;\r\n        }\r\n        \r\n        if (!isset($context[$condElem])) {\r\n            $this-&gt;_error(self::COND_ELEM_NOT_IN_CONTEXT);\r\n            return false;\r\n        }\r\n        \r\n        /**\r\n         * if $condElemVal is not set return false if the conditional element value \r\n         * is not empty and $value is empty\r\n         */\r\n        if (!isset($condElemVal) &amp;&amp; !empty($context[$condElem]) &amp;&amp; !parent::isValid($value)) {\r\n            $this-&gt;_error(self::REQUIRED);\r\n            return false;\r\n        }\r\n        \r\n        if (($condElemVal == $context[$condElem]) &amp;&amp; (!parent::isValid($value))){\r\n            $this-&gt;_error(self::REQUIRED);\r\n            return false;\r\n        }\r\n        \r\n        return true;\r\n    }\r\n}\r\n</pre>\r\n\r\n<h4>Usage:</h4>\r\n\r\n<p>The following code will make the credit card text field required if the value of a field called \"paymentMethod\" is set to 1.</p>\r\n<pre class=\"brush:php\">\r\n$this->addElement(\'text\', \'ccnum\', array(\r\n    \'placeholder\'         => \'Credit Card Number*\',\r\n    \'filters\'       => array(\'StringTrim\', \'StripTags\'),\r\n    \'allowEmpty\'    => false,\r\n    \'validators\'    => array(\r\n        array(\r\n            \'RequiredIfConditionalElementSet\', true, array( \r\n                \'conditionalElement\' => \'paymentMethod\',\r\n                \'conditionalElementValue\' => 1,\r\n            ),\r\n        ),\r\n    ),\r\n    \'decorators\'    => array(\'ViewHelper\',\'Errors\'),\r\n));\r\n</pre>\r\n\r\n<p>You pass in \'conditionalElement\' which contains the name of the conditional element which has to be set in order for your element to be required; and you pass in \'conditionalElementValue\'  which contains the value the conditional element has to be set to in order for your element to be required.  When \'conditionalElementValue\' is not passed into the validator, the validator will make your element required as long as the conditional element is not empty.</p>\r\n\r\n<p>\r\n<em>NB:</em> The following is how I told my form how to locate my validator.  <span style=\"font-style:italic\">$this</span> represents the form object.\r\n</p>\r\n\r\n<pre class=\"brush:php\">\r\n$this->addElementPrefixPath(\'My_Validate\', \'My/Validate/\', \'validate\');\r\n</pre>\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:5515:\"<p>This validator class makes a form element \"required\" if and only if another element is set.</p>\n<pre class=\"brush:php\">\n&lt;?php\n\n/**\n * Requires an element if conditional elemet is set\n *\n * @author rmuktader\n */\nclass My_Validate_RequiredIfConditionalElementSet extends Zend_Validate_NotEmpty\n{\n     /**\n     * Error codes\n     * @const string\n     */\n    const REQUIRED          = \'required\';\n    \n    const MISSING_COND_ELEM = \'missingCondElem\';\n    \n    const CONTEXT_NULL      = \'contextNull\';\n    \n    const CONTEXT_NOT_ARRAY = \'contextNotArray\';\n    \n    const COND_ELEM_NOT_IN_CONTEXT = \'condElemNotInContext\';\n    \n\n    /**\n     * Error messages\n     * @var array\n     */\n    protected $_messageTemplates = array(\n        self::REQUIRED          =&gt; \'Value is required and can\\\'t be empty\',\n        self::MISSING_COND_ELEM =&gt; \'No conditional element provided\',\n        self::CONTEXT_NULL      =&gt; \'The context is null\',\n        self::CONTEXT_NOT_ARRAY =&gt; \'The context is not an array\',\n        self::COND_ELEM_NOT_IN_CONTEXT  =&gt; \'The conditional element was not found in the context array\',\n    );\n    \n    protected $condElem = null;\n    protected $condElemVal = null;\n    \n    public function __construct($options)\n    {\n        if (isset($options[\'conditionalElement\']) &amp;&amp;\n           !empty($options[\'conditionalElement\'])) {\n            \n               $this-&gt;setCondElem($options[\'conditionalElement\']);\n        } \n       \n       \n       if (isset($options[\'conditionalElementValue\'])) {\n\n            $this-&gt;setCondElemVal($options[\'conditionalElementValue\']);\n           \n        } \n       \n    }\n    \n    /**\n     * Retrieve conditional element\n     *\n     * @return string\n     */\n    public function getCondElem() {\n        return $this-&gt;condElem;\n    }\n\n    /**\n     * @param  string $condElem\n     * @return My_Validate_RequiredIfConditionalElementSet\n     */\n    public function setCondElem($condElem) {\n        $this-&gt;condElem = (string) $condElem;\n        $this-&gt;condElem = trim($this-&gt;condElem);\n        return $this;\n    }\n\n    /**\n     * Retrieve conditional element value\n     *\n     * @return string\n     */\n    public function getCondElemVal()\n    {\n        return $this-&gt;condElemVal;\n    }\n    \n    /**\n     * @param  string $condElemVal\n     * @return My_Validate_RequiredIfConditionalElementSet\n     */\n    public function setCondElemVal($condElemVal)\n    {\n        $this-&gt;condElemVal = $condElemVal;\n        return $this;\n    }\n    \n    /**\n     * Defined by Zend_Validate_Interface\n     *\n     * @param  string $value\n     * @param array $context.  When used with a form this array contains the names of the form elements \n     * as keys and the corresponding form element values as the array values.\n     *\n     * @return boolean\n     */\n    public function isValid($value, $context = null)\n    {\n        $condElem = $this-&gt;getCondElem();\n        $condElemVal = $this-&gt;getCondElemVal();\n     \n        if ($condElem === null) {\n            $this-&gt;_error(self::MISSING_COND_ELEM);\n            return false;\n        }\n        \n        if ($context === null) {\n            $this-&gt;_error(self::CONTEXT_NULL);\n            return false;\n        }\n        \n        if (!is_array($context)) {\n            $this-&gt;_error(self::CONTEXT_NOT_ARRAY);\n            return false;\n        }\n        \n        if (!isset($context[$condElem])) {\n            $this-&gt;_error(self::COND_ELEM_NOT_IN_CONTEXT);\n            return false;\n        }\n        \n        /**\n         * if $condElemVal is not set return false if the conditional element value \n         * is not empty and $value is empty\n         */\n        if (!isset($condElemVal) &amp;&amp; !empty($context[$condElem]) &amp;&amp; !parent::isValid($value)) {\n            $this-&gt;_error(self::REQUIRED);\n            return false;\n        }\n        \n        if (($condElemVal == $context[$condElem]) &amp;&amp; (!parent::isValid($value))){\n            $this-&gt;_error(self::REQUIRED);\n            return false;\n        }\n        \n        return true;\n    }\n}\n</pre><h4>Usage:</h4>\n<p>The following code will make the credit card text field required if the value of a field called \"paymentMethod\" is set to 1.</p>\n<pre class=\"brush:php\">\n$this-&gt;addElement(\'text\', \'ccnum\', array(\n    \'placeholder\'         =&gt; \'Credit Card Number*\',\n    \'filters\'       =&gt; array(\'StringTrim\', \'StripTags\'),\n    \'allowEmpty\'    =&gt; false,\n    \'validators\'    =&gt; array(\n        array(\n            \'RequiredIfConditionalElementSet\', true, array( \n                \'conditionalElement\' =&gt; \'paymentMethod\',\n                \'conditionalElementValue\' =&gt; 1,\n            ),\n        ),\n    ),\n    \'decorators\'    =&gt; array(\'ViewHelper\',\'Errors\'),\n));\n</pre><p>You pass in \'conditionalElement\' which contains the name of the conditional element which has to be set in order for your element to be required; and you pass in \'conditionalElementValue\'  which contains the value the conditional element has to be set to in order for your element to be required.  When \'conditionalElementValue\' is not passed into the validator, the validator will make your element required as long as the conditional element is not empty.</p>\n<p>\n<em>NB:</em> The following is how I told my form how to locate my validator.  <span style=\"font-style:italic\">$this</span> represents the form object.\n</p>\n<pre class=\"brush:php\">\n$this-&gt;addElementPrefixPath(\'My_Validate\', \'My/Validate/\', \'validate\');\n</pre>\";s:12:\"safe_summary\";s:0:\"\";}}}s:10:\"field_tags\";a:1:{s:3:\"und\";a:2:{i:0;a:1:{s:3:\"tid\";s:1:\"3\";}i:1;a:1:{s:3:\"tid\";s:1:\"4\";}}}s:11:\"field_image\";a:0:{}}',0,1374797661,1),('field:node:2','a:3:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:3282:\"<p>The first step towards building a software with solid architecture is identifying the purpose of that software.  Sounds simple enough but being able to discern such things separates you from hoards of bad programmers.</p>\r\n\r\n<p>In my teenage years my assumption was that software\'s purpose is to save people time.  It\'s suppose to do monotonous and uncreative things that are beneath humans.  This is a good thesis but it does not encompass the entirety of why we build software.  If you look at it from a purist\'s perspective people who track endangered species or smash atoms aren\'t concerned with time.  So, what is the purpose of building software for them?</p>\r\n\r\n<p>My understanding of this matured with experience.  Recently, I found that my evolved thesis resonated with what Max Kanat-Alexander wrote in Code Simplicity:The Science of Software Development(O’Reilly), copyright 2012, 978-1-4493-1389-0.  I don\'t dare try to be as eloquent as Kanat-Alexander so I will use some his words to explain my points.   \r\n</p>\r\n\r\n<p><strong>The primary purpose of all software is to <a href=\"http://dictionary.reference.com/browse/help\" title=\"Definition\">help</a> people.</strong>  You can break this down into a more specific purpose for your individual piece of software.  For example, an on-line dictionary exists to help people find meanings for words.  An accounting software exists to help people keep track of their finances.  A software that autonomously helps the environment exists to help <em>people</em> to help the environment.  Software doesn\'t exist to help computers, it doesn\'t exist to help buildings or any other \"thing\". </p>\r\n\r\n<p>The primary purpose of software cannot be <em>\"make me money\"</em>.  This motivation does not yield optimal result in any creative profession.  DeCartes could not have produced something better than \"I think therefore I am\" if someone paid him a million dollar.  He could not have produced it faster either.  Here is a <a href=\"http://fora.tv/2010/01/27/Daniel_Pink_The_Surprising_Truth_About_What_Motivates_Us\">link</a> detailing what am talking about.  The purpose of software is certainly not to take people\'s jobs away.  New technology shifts jobs and increases net jobs.  Otherwise,  if we count all the new inventions starting from the invention of the wheel then the net jobs on earth should come out to be zero.  If you don\'t agree with this then you might want to learn about the <a href=\"http://en.wikipedia.org/wiki/Luddite\">Luddites</a> and the <a href=\"http://en.wikipedia.org/wiki/Luddite_fallacy\">Luddite fallacy</a>.</p>\r\n\r\n<p>Use the purpose of the software to determine the goals of your software.  If you are writing a software that allows students to register for courses then your goal is <em>to help students register for courses</em>.  You should write down your goal(s) so there is no miscommunication within your team or between your client and you.  If your client wants outlandish features then go ahead and humor her as long as those features don\'t conflict or obscure your goal(s).  When you are buried in work prioritize by determining which feature or bug fix will help people the most.  If can do this then you have the beginning of a good software that will be appreciated by your client.</p>\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:3264:\"<p>The first step towards building a software with solid architecture is identifying the purpose of that software.  Sounds simple enough but being able to discern such things separates you from hoards of bad programmers.</p>\n<p>In my teenage years my assumption was that software\'s purpose is to save people time.  It\'s suppose to do monotonous and uncreative things that are beneath humans.  This is a good thesis but it does not encompass the entirety of why we build software.  If you look at it from a purist\'s perspective people who track endangered species or smash atoms aren\'t concerned with time.  So, what is the purpose of building software for them?</p>\n<p>My understanding of this matured with experience.  Recently, I found that my evolved thesis resonated with what Max Kanat-Alexander wrote in Code Simplicity:The Science of Software Development(O’Reilly), copyright 2012, 978-1-4493-1389-0.  I don\'t dare try to be as eloquent as Kanat-Alexander so I will use some his words to explain my points.\n</p>\n<p><strong>The primary purpose of all software is to <a href=\"http://dictionary.reference.com/browse/help\" title=\"Definition\">help</a> people.</strong>  You can break this down into a more specific purpose for your individual piece of software.  For example, an on-line dictionary exists to help people find meanings for words.  An accounting software exists to help people keep track of their finances.  A software that autonomously helps the environment exists to help <em>people</em> to help the environment.  Software doesn\'t exist to help computers, it doesn\'t exist to help buildings or any other \"thing\". </p>\n<p>The primary purpose of software cannot be <em>\"make me money\"</em>.  This motivation does not yield optimal result in any creative profession.  DeCartes could not have produced something better than \"I think therefore I am\" if someone paid him a million dollar.  He could not have produced it faster either.  Here is a <a href=\"http://fora.tv/2010/01/27/Daniel_Pink_The_Surprising_Truth_About_What_Motivates_Us\">link</a> detailing what am talking about.  The purpose of software is certainly not to take people\'s jobs away.  New technology shifts jobs and increases net jobs.  Otherwise,  if we count all the new inventions starting from the invention of the wheel then the net jobs on earth should come out to be zero.  If you don\'t agree with this then you might want to learn about the <a href=\"http://en.wikipedia.org/wiki/Luddite\">Luddites</a> and the <a href=\"http://en.wikipedia.org/wiki/Luddite_fallacy\">Luddite fallacy</a>.</p>\n<p>Use the purpose of the software to determine the goals of your software.  If you are writing a software that allows students to register for courses then your goal is <em>to help students register for courses</em>.  You should write down your goal(s) so there is no miscommunication within your team or between your client and you.  If your client wants outlandish features then go ahead and humor her as long as those features don\'t conflict or obscure your goal(s).  When you are buried in work prioritize by determining which feature or bug fix will help people the most.  If can do this then you have the beginning of a good software that will be appreciated by your client.</p>\n\";s:12:\"safe_summary\";s:0:\"\";}}}s:10:\"field_tags\";a:1:{s:3:\"und\";a:2:{i:0;a:1:{s:3:\"tid\";s:1:\"1\";}i:1;a:1:{s:3:\"tid\";s:1:\"2\";}}}s:11:\"field_image\";a:0:{}}',0,1348246447,1),('field:node:3','a:3:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:6180:\"<p>One of the basic tasks of web developers is to build forms.  However, validating each and every form field in PHP can be tedious.  I have seen people put the POST array through a for loop and try to validate everything in one go.  But that solution is neither robust nor versatile. </p>\r\n<p>Zend Framework has one of the best solution for building secure and versatile forms in PHP.  Unfortunately, it\'s not transparent how to use the Zend_Form component as a stand alone library without Zend MVC.  In this tutorial I\'ll try to document how to use Zend_Form with plain PHP and give the front end person full control over the HTML.</p>\r\n<p>\r\n1. First place the Zend folder in your working directory.  You\'ll find it inside the library folder of your Zend Framework download. <br />\r\n2. Then create a create a form.php file with the following code.\r\n</p>\r\n<pre class=\"brush:php\">\r\n&lt;?php\r\n/**\r\n * @file form.php\r\n */\r\n \r\n// Add Zend Framework to the include path\r\nset_include_path(\r\n        implode(PATH_SEPARATOR, array(\r\n                get_include_path(),\r\n                __DIR__ .\'/\'\r\n        ))\r\n);\r\n        \r\n// Create the auto loader so zend can load the rest automatically\r\nrequire_once \'Zend/Loader/Autoloader.php\';\r\n$autoloader = Zend_Loader_Autoloader::getInstance();\r\n    \r\n$form = new Zend_Form;\r\n\r\n// Set form name, id, method and action\r\n$form-&gt;setName( \'contact\' )\r\n    -&gt;setAttrib( \'id\', \'form-contact\' )\r\n    -&gt;setMethod( \'post\' )\r\n    -&gt;setAction( \'http://\'.\r\n        $_SERVER[\'SERVER_NAME\'] . $_SERVER[\'REQUEST_URI\'] \r\n    );\r\n\r\n// Create text box for name\r\n$name = new Zend_Form_Element_Text( \'name\' );\r\n$name-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'fullname\' )\r\n    -&gt;addErrorMessage( \'Please provide your name\' );\r\n\r\n// Create text box for email        \r\n$email = new Zend_Form_Element_Text( \'email\' );\r\n$email-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'email\' )\r\n    -&gt;addErrorMessage( \'Please provide a valid e-mail address\' );\r\n\r\n// Create text area for message    \r\n$message = new Zend_Form_Element_Textarea( \'message\' );\r\n$message-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'message\' )\r\n    -&gt;addErrorMessage( \'Please specify a message\' );\r\n\r\n// Create a submit button    \r\n$submit = new Zend_Form_Element_Submit( \'submit\' );\r\n$submit-&gt;setLabel( \'Send Your Message!\' )\r\n    -&gt;setAttrib( \'id\', \'submit\' );\r\n\r\n// Set the ViewScript decorator for the form and tell it which \r\n// template file to use\r\n$form-&gt;setDecorators( array( \r\n    array( \'ViewScript\', array( \'viewScript\' =&gt; \'form-contact.tpl.php\' ) ) ) );\r\n\r\n// Add the form elements AFTER the viewscript decorator has been set\r\n$form-&gt;addElements( array( $name, $email, $message, $submit ) );\r\n\r\n// Get rid of all element decorators except for ViewHelper to render\r\n// the individual elements and Errors decorator to render the errors.\r\n$form-&gt;setElementDecorators( array( \'ViewHelper\', \'Errors\' ) );\r\n    \r\n// Create an instance of Zend_View and set the directory \r\n// for the template files\r\n$view = new Zend_View();\r\n$view-&gt;setScriptPath( __DIR__ );\r\n\r\n// Tell all the elements in the form which view to use when rendering\r\nforeach ($form as $item){\r\n    $item-&gt;setView($view);\r\n}\r\n\r\n// process or display the form\r\nif( isset( $_POST[\'submit\'] ) &amp;&amp; ( $form-&gt;isValid( $_POST ) ) ) {\r\n    echo \'Thank you\';\r\n} else {\r\n    echo $form-&gt;render( $view );\r\n}\r\n</pre>\r\n<p>\r\n3. Create  form-contact.tpl.php in the same directory with the following code.\r\n</p>\r\n<pre class=\"brush:php\">\r\n&lt;?php\r\n/**\r\n * @file form-contact.tpl.php\r\n */\r\n?&gt;\r\n&lt;!doctype html&gt;  \r\n&lt;html lang=&quot;en&quot;&gt;  \r\n&lt;head&gt;  \r\n&lt;meta charset=&quot;utf-8&quot;&gt;  \r\n&lt;title&gt;Stand Alone Zend Form&lt;/title&gt;  \r\n&lt;meta name=&quot;description&quot; content=&quot;Stand Alone Zend Form&quot;&gt;  \r\n&lt;meta name=&quot;author&quot; content=&quot;Rayhan Muktader&quot;&gt;  \r\n&lt;!--[if lt IE 9]&gt;  \r\n&lt;script src=&quot;http://html5shiv.googlecode.com/svn/trunk/html5.js&quot;&gt;&lt;/script&gt;  \r\n&lt;![endif]--&gt;  \r\n&lt;/head&gt;  \r\n&lt;body&gt;  \r\n      \r\n    &lt;form id=&quot;contact&quot; action=&quot;&lt;?= $this-&gt;element-&gt;getAction(); ?&gt;&quot; \r\n        method=&quot;&lt;?= $this-&gt;element-&gt;getMethod(); ?&gt;&quot;&gt;\r\n\r\n        &lt;table&gt;\r\n            &lt;tr&gt;\r\n                &lt;td&gt;Your Name&lt;/td&gt;\r\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;name; ?&gt;&lt;/td&gt;\r\n            &lt;/tr&gt;\r\n\r\n            &lt;tr&gt;\r\n                &lt;td&gt;Your E-mail Address&lt;/td&gt;\r\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;email; ?&gt;&lt;/td&gt;\r\n            &lt;/tr&gt;\r\n\r\n            &lt;tr&gt;\r\n                &lt;td&gt;Your Message&lt;/td&gt;\r\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;message; ?&gt;&lt;/td&gt;\r\n            &lt;/tr&gt;\r\n\r\n            &lt;tr&gt;\r\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;submit ?&gt;&lt;/td&gt;\r\n            &lt;/tr&gt;\r\n        &lt;/table&gt;\r\n    &lt;/form&gt;\r\n    \r\n&lt;/body&gt;  \r\n&lt;/html&gt;  \r\n</pre>\r\n<p>Visit form.php in your browser to see your working form in all its glory.</p>\r\n<p>Everything should make sense if you read through the code.  It\'s well documented.  However,  I\'ll mention the the secret sauces that make this work.</p>\r\n<ol>\r\n  <li>Line 7 through 16 is how you load the Zend library manually.</li>\r\n  <li>Line 53 tells Zend Framework which template file to use when rendering the form.</li>\r\n  <li>In line 57 you add the elements to the form <em>after</em> the template fie has been set.</li>\r\n  <li>Line 61 shows how to remove all form decorators except for <em>ViewHelper</em> to render the form elements and <em>Errors</em> to render the form errors.</li>\r\n  <li>In lines 65-66, you create a Zend_View object and tell which directory the template file is.</li>\r\n  <li>In lines 69-71, you tell the form elements which view object to use when rendering.</li>\r\n</ol>\r\n<p>Here\'s a live <a href=\"/examples/zf-stand-alone-form/form.php\">demo</a> and the <a href=\"https://github.com/rmuktader/zf-stand-alone-form\">code</a> for your convenience.</p>\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:6009:\"<p>One of the basic tasks of web developers is to build forms.  However, validating each and every form field in PHP can be tedious.  I have seen people put the POST array through a for loop and try to validate everything in one go.  But that solution is neither robust nor versatile. </p>\n<p>Zend Framework has one of the best solution for building secure and versatile forms in PHP.  Unfortunately, it\'s not transparent how to use the Zend_Form component as a stand alone library without Zend MVC.  In this tutorial I\'ll try to document how to use Zend_Form with plain PHP and give the front end person full control over the HTML.</p>\n<p>\n1. First place the Zend folder in your working directory.  You\'ll find it inside the library folder of your Zend Framework download. <br />\n2. Then create a create a form.php file with the following code.\n</p>\n<pre class=\"brush:php\">\n&lt;?php\n/**\n * @file form.php\n */\n \n// Add Zend Framework to the include path\nset_include_path(\n        implode(PATH_SEPARATOR, array(\n                get_include_path(),\n                __DIR__ .\'/\'\n        ))\n);\n        \n// Create the auto loader so zend can load the rest automatically\nrequire_once \'Zend/Loader/Autoloader.php\';\n$autoloader = Zend_Loader_Autoloader::getInstance();\n    \n$form = new Zend_Form;\n\n// Set form name, id, method and action\n$form-&gt;setName( \'contact\' )\n    -&gt;setAttrib( \'id\', \'form-contact\' )\n    -&gt;setMethod( \'post\' )\n    -&gt;setAction( \'http://\'.\n        $_SERVER[\'SERVER_NAME\'] . $_SERVER[\'REQUEST_URI\'] \n    );\n\n// Create text box for name\n$name = new Zend_Form_Element_Text( \'name\' );\n$name-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'fullname\' )\n    -&gt;addErrorMessage( \'Please provide your name\' );\n\n// Create text box for email        \n$email = new Zend_Form_Element_Text( \'email\' );\n$email-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'email\' )\n    -&gt;addErrorMessage( \'Please provide a valid e-mail address\' );\n\n// Create text area for message    \n$message = new Zend_Form_Element_Textarea( \'message\' );\n$message-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'message\' )\n    -&gt;addErrorMessage( \'Please specify a message\' );\n\n// Create a submit button    \n$submit = new Zend_Form_Element_Submit( \'submit\' );\n$submit-&gt;setLabel( \'Send Your Message!\' )\n    -&gt;setAttrib( \'id\', \'submit\' );\n\n// Set the ViewScript decorator for the form and tell it which \n// template file to use\n$form-&gt;setDecorators( array( \n    array( \'ViewScript\', array( \'viewScript\' =&gt; \'form-contact.tpl.php\' ) ) ) );\n\n// Add the form elements AFTER the viewscript decorator has been set\n$form-&gt;addElements( array( $name, $email, $message, $submit ) );\n\n// Get rid of all element decorators except for ViewHelper to render\n// the individual elements and Errors decorator to render the errors.\n$form-&gt;setElementDecorators( array( \'ViewHelper\', \'Errors\' ) );\n    \n// Create an instance of Zend_View and set the directory \n// for the template files\n$view = new Zend_View();\n$view-&gt;setScriptPath( __DIR__ );\n\n// Tell all the elements in the form which view to use when rendering\nforeach ($form as $item){\n    $item-&gt;setView($view);\n}\n\n// process or display the form\nif( isset( $_POST[\'submit\'] ) &amp;&amp; ( $form-&gt;isValid( $_POST ) ) ) {\n    echo \'Thank you\';\n} else {\n    echo $form-&gt;render( $view );\n}\n</pre><p>\n3. Create  form-contact.tpl.php in the same directory with the following code.\n</p>\n<pre class=\"brush:php\">\n&lt;?php\n/**\n * @file form-contact.tpl.php\n */\n?&gt;\n&lt;!doctype html&gt;  \n&lt;html lang=\"en\"&gt;  \n&lt;head&gt;  \n&lt;meta charset=\"utf-8\"&gt;  \n&lt;title&gt;Stand Alone Zend Form&lt;/title&gt;  \n&lt;meta name=\"description\" content=\"Stand Alone Zend Form\"&gt;  \n&lt;meta name=\"author\" content=\"Rayhan Muktader\"&gt;  \n&lt;!--[if lt IE 9]&gt;  \n&lt;script src=\"<a href=\"http://html5shiv.googlecode.com/svn/trunk/html5.js&quot;&gt;&lt;/script&gt;\">http://html5shiv.googlecode.com/svn/trunk/html5.js\"&gt;&lt;/script&gt;</a>  \n&lt;![endif]--&gt;  \n&lt;/head&gt;  \n&lt;body&gt;  \n      \n    &lt;form id=\"contact\" action=\"&lt;?= $this-&gt;element-&gt;getAction(); ?&gt;\" \n        method=\"&lt;?= $this-&gt;element-&gt;getMethod(); ?&gt;\"&gt;\n\n        &lt;table&gt;\n            &lt;tr&gt;\n                &lt;td&gt;Your Name&lt;/td&gt;\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;name; ?&gt;&lt;/td&gt;\n            &lt;/tr&gt;\n\n            &lt;tr&gt;\n                &lt;td&gt;Your E-mail Address&lt;/td&gt;\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;email; ?&gt;&lt;/td&gt;\n            &lt;/tr&gt;\n\n            &lt;tr&gt;\n                &lt;td&gt;Your Message&lt;/td&gt;\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;message; ?&gt;&lt;/td&gt;\n            &lt;/tr&gt;\n\n            &lt;tr&gt;\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;submit ?&gt;&lt;/td&gt;\n            &lt;/tr&gt;\n        &lt;/table&gt;\n    &lt;/form&gt;\n    \n&lt;/body&gt;  \n&lt;/html&gt;  \n</pre><p>Visit form.php in your browser to see your working form in all its glory.</p>\n<p>Everything should make sense if you read through the code.  It\'s well documented.  However,  I\'ll mention the the secret sauces that make this work.</p>\n<ol><li>Line 7 through 16 is how you load the Zend library manually.</li>\n<li>Line 53 tells Zend Framework which template file to use when rendering the form.</li>\n<li>In line 57 you add the elements to the form <em>after</em> the template fie has been set.</li>\n<li>Line 61 shows how to remove all form decorators except for <em>ViewHelper</em> to render the form elements and <em>Errors</em> to render the form errors.</li>\n<li>In lines 65-66, you create a Zend_View object and tell which directory the template file is.</li>\n<li>In lines 69-71, you tell the form elements which view object to use when rendering.</li>\n</ol><p>Here\'s a live <a href=\"/examples/zf-stand-alone-form/form.php\">demo</a> and the <a href=\"https://github.com/rmuktader/zf-stand-alone-form\">code</a> for your convenience.</p>\n\";s:12:\"safe_summary\";s:0:\"\";}}}s:10:\"field_tags\";a:1:{s:3:\"und\";a:2:{i:0;a:1:{s:3:\"tid\";s:1:\"3\";}i:1;a:1:{s:3:\"tid\";s:1:\"4\";}}}s:11:\"field_image\";a:0:{}}',0,1348246447,1),('field:node:4','a:3:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:4803:\"Git has to be by far my favorite version control system.  Here are the git commands I use ninety percent of the time.  There are many other git commands(e.g., reflog and stash) and variation of the commands I listed that might be considered essentials by others. However, I get by with just the following most of the time. \r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit init\r\n</pre> will convert a directory into a git repository.  It will create directory called .git and save all git related data in it.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit add .\r\n</pre> will add all the content of the directory and sub directories to the repository\'s index(staging area).  This will allow git to track the files.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit status\r\n</pre> will tell you which files are being tracked and which ones are not, the differences between the working directory and the index, the difference between the the last commit and the index.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit commit -m \'initial commit\'\r\n</pre> will commit what\'s in the index.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit commit -am \'second file\'\r\n</pre> will add the content of the working directory to the index and commit it.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit add -p\r\n</pre>will interactively show you the changes you have made in the working directory and ask you if you want stage the changes.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit rm -r one-of-the-directories\r\ngit commit -m \"Removed a directory\"\r\n</pre> will remove a directory.  The rm command can be used to delete file the same  way.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit log\r\n</pre> will display the history of the repository.  Each commit has forty character sha1 hash that is used as an identifier.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit diff\r\n</pre> shows you the difference between your index and your working directory.  Basically it will show you what you have changed but have not staged yet.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit diff --staged\r\n</pre> Shows you your staged content that will go into your next commit.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit diff commit_x commit_y\r\n</pre> Displays the differences between two commits or branches.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit checkout -b feature_x\r\n</pre> creates a new branch feature_x if it doesn\'t exist and sets your working directory and index to it.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit checkout branch_name\r\n</pre> will simply check the branch and update your working directory and your index.  If your working directory has files that are not tracked then they those files will unaffected.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit checkout SHA1_for_commit path/to/file.ext\r\n</pre> checks out a specific file from a specific commit.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit branch -v\r\n</pre> displays list of branches and their last commits.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit merge feature_x\r\n</pre> will merge the changes in the feature_x branch with the master branch.  Basically it\'s creating a new commit that has two parents, the last commit of the master branch and the last commit of the feature_x branch.  In a fast forward merge where the master branch hasn\'t been changed the the latest commit simply moves the end of the master branch and the latest commit simply has only one parent.  Git will inform you if the merge causes conflict.  You are expected to manually \r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit rebase\r\n</pre> will flatten two forked branches into one branch with a linear history.  When you merge two commits Master5 and Forked5 it will create a new commit Master5\' that will contain the changes made in both Master5 and Forked5.  However, when you rebase you will create Forked1\', Forked2\', Forked3\', Forked4\', Forked5\' and tack them to the end of Master5.  Rebase only works on local repositories.   \r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit clone /path/to/repository\r\n</pre> will copy a local repository.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit clone git(@)github.com:username/repo.git\r\n</pre>\r\nwill copy remote private repository.  You will require a SSH key.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit clone http:// github.com/username/repo.git\r\n</pre>\r\nwill copy remote public repository.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit pull origin\r\n</pre>\r\nwill update a local repository with changes from a Github repository.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit remote add origin git(@)github.com:username/foo.git\r\n</pre> adds a remote repository to your existing local repository.\r\n\r\n<pre class=\"brush:php;gutter: false;\">\r\ngit push -u origin my_branch\r\n</pre> push local changes to remote repository.\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:4850:\"<p>Git has to be by far my favorite version control system.  Here are the git commands I use ninety percent of the time.  There are many other git commands(e.g., reflog and stash) and variation of the commands I listed that might be considered essentials by others. However, I get by with just the following most of the time. </p>\n<pre class=\"brush:php;gutter: false;\">\ngit init\n</pre><p> will convert a directory into a git repository.  It will create directory called .git and save all git related data in it.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit add .\n</pre><p> will add all the content of the directory and sub directories to the repository\'s index(staging area).  This will allow git to track the files.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit status\n</pre><p> will tell you which files are being tracked and which ones are not, the differences between the working directory and the index, the difference between the the last commit and the index.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit commit -m \'initial commit\'\n</pre><p> will commit what\'s in the index.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit commit -am \'second file\'\n</pre><p> will add the content of the working directory to the index and commit it.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit add -p\n</pre><p>will interactively show you the changes you have made in the working directory and ask you if you want stage the changes.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit rm -r one-of-the-directories\ngit commit -m \"Removed a directory\"\n</pre><p> will remove a directory.  The rm command can be used to delete file the same  way.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit log\n</pre><p> will display the history of the repository.  Each commit has forty character sha1 hash that is used as an identifier.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit diff\n</pre><p> shows you the difference between your index and your working directory.  Basically it will show you what you have changed but have not staged yet.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit diff --staged\n</pre><p> Shows you your staged content that will go into your next commit.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit diff commit_x commit_y\n</pre><p> Displays the differences between two commits or branches.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit checkout -b feature_x\n</pre><p> creates a new branch feature_x if it doesn\'t exist and sets your working directory and index to it.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit checkout branch_name\n</pre><p> will simply check the branch and update your working directory and your index.  If your working directory has files that are not tracked then they those files will unaffected.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit checkout SHA1_for_commit path/to/file.ext\n</pre><p> checks out a specific file from a specific commit.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit branch -v\n</pre><p> displays list of branches and their last commits.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit merge feature_x\n</pre><p> will merge the changes in the feature_x branch with the master branch.  Basically it\'s creating a new commit that has two parents, the last commit of the master branch and the last commit of the feature_x branch.  In a fast forward merge where the master branch hasn\'t been changed the the latest commit simply moves the end of the master branch and the latest commit simply has only one parent.  Git will inform you if the merge causes conflict.  You are expected to manually </p>\n<pre class=\"brush:php;gutter: false;\">\ngit rebase\n</pre><p> will flatten two forked branches into one branch with a linear history.  When you merge two commits Master5 and Forked5 it will create a new commit Master5\' that will contain the changes made in both Master5 and Forked5.  However, when you rebase you will create Forked1\', Forked2\', Forked3\', Forked4\', Forked5\' and tack them to the end of Master5.  Rebase only works on local repositories.   </p>\n<pre class=\"brush:php;gutter: false;\">\ngit clone /path/to/repository\n</pre><p> will copy a local repository.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit clone git(@)github.com:username/repo.git\n</pre><p>will copy remote private repository.  You will require a SSH key.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit clone http:// github.com/username/repo.git\n</pre><p>will copy remote public repository.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit pull origin\n</pre><p>will update a local repository with changes from a Github repository.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit remote add origin git(@)github.com:username/foo.git\n</pre><p> adds a remote repository to your existing local repository.</p>\n<pre class=\"brush:php;gutter: false;\">\ngit push -u origin my_branch\n</pre><p> push local changes to remote repository.</p>\n\";s:12:\"safe_summary\";s:0:\"\";}}}s:10:\"field_tags\";a:1:{s:3:\"und\";a:1:{i:0;a:1:{s:3:\"tid\";s:1:\"5\";}}}s:11:\"field_image\";a:0:{}}',0,1348246447,1),('field:node:5','a:3:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:1075:\"Here is a snippet of code that I have been using to post data programmatically for years.\r\n<pre class=\"brush:php\">\r\n/**\r\n* posts request to an url\r\n* @param $url where to post to\r\n* @param $fields array containing all the field names and values\r\n* @return $response raw response sent by server after post\r\n*/\r\nfunction do_post_request( $url = NULL , $fields )\r\n{\r\n	if ( $url === NULL ) {\r\n		throw new Exception( \'Cannot post data. URL is NULL.\' );\r\n	}\r\n\r\n    //url-ify the data for the POST\r\n    foreach ( $fields as $key => $value ) {\r\n		$fields_string .= $key.\'=\'.urlencode( $value ).\'&\';\r\n	}\r\n\r\n	$fields_string = rtrim($fields_string,\'&\');\r\n\r\n    //open connection\r\n    $ch = curl_init();\r\n\r\n    //set the url, number of POST vars, POST data\r\n    curl_setopt($ch,CURLOPT_URL,$url);\r\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\r\n    curl_setopt($ch,CURLOPT_POST,count($fields));\r\n    curl_setopt($ch,CURLOPT_POSTFIELDS,$fields_string);\r\n\r\n    //execute post\r\n    $result = curl_exec($ch);\r\n    //close connection\r\n    curl_close($ch);\r\n\r\n    return $result;\r\n}\r\n</pre>\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:1056:\"<p>Here is a snippet of code that I have been using to post data programmatically for years.</p>\n<pre class=\"brush:php\">\n/**\n* posts request to an url\n* @param $url where to post to\n* @param $fields array containing all the field names and values\n* @return $response raw response sent by server after post\n*/\nfunction do_post_request( $url = NULL , $fields )\n{\n	if ( $url === NULL ) {\n		throw new Exception( \'Cannot post data. URL is NULL.\' );\n	}\n\n    //url-ify the data for the POST\n    foreach ( $fields as $key =&gt; $value ) {\n		$fields_string .= $key.\'=\'.urlencode( $value ).\'&amp;\';\n	}\n\n	$fields_string = rtrim($fields_string,\'&amp;\');\n\n    //open connection\n    $ch = curl_init();\n\n    //set the url, number of POST vars, POST data\n    curl_setopt($ch,CURLOPT_URL,$url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    curl_setopt($ch,CURLOPT_POST,count($fields));\n    curl_setopt($ch,CURLOPT_POSTFIELDS,$fields_string);\n\n    //execute post\n    $result = curl_exec($ch);\n    //close connection\n    curl_close($ch);\n\n    return $result;\n}\n</pre>\";s:12:\"safe_summary\";s:0:\"\";}}}s:10:\"field_tags\";a:1:{s:3:\"und\";a:1:{i:0;a:1:{s:3:\"tid\";s:1:\"6\";}}}s:11:\"field_image\";a:0:{}}',0,1348246447,1),('field:node:6','a:3:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:16830:\"<p>\r\nI went over how to create <a href=\"/stand-alone-zend-form-example\">stand alone zend forms</a> in a previous post.  This time lets discuss how to build stand alone zend forms with custom composite elements.\r\n</p>\r\n<p>\r\nI had to build a form that has to collect social security numbers.  The client needed a form that would work without javascript and had separate fields for each section of a SSN.  \r\n<pre class=\"brush:xml;\">\r\n&lt;input type=&quot;text&quot; name=&quot;ssn[areanum]&quot; id=&quot;ssn-areanum&quot; value=&quot;&quot; size=&quot;3&quot; maxlength=&quot;3&quot;&gt;\r\n\r\n&lt;input type=&quot;text&quot; name=&quot;ssn[groupnum]&quot; id=&quot;ssn-groupnum&quot; value=&quot;&quot; size=&quot;2&quot; maxlength=&quot;2&quot;&gt;\r\n\r\n&lt;input type=&quot;text&quot; name=&quot;ssn[serialnum]&quot; id=&quot;ssn-serialnum&quot; value=&quot;&quot; size=&quot;4&quot; maxlength=&quot;4&quot;&gt;\r\n</pre>\r\n</p>\r\n<p>\r\nTo my pleasant surprise, I found my solution on Jon Lebensold\'s site <a href=\"http://zendcasts.com\">zendcasts.com</a> where there are video tutorials on how to build a composite zend form element that collects phone numbers.  This is where I learned how to do the things I am documenting in this tutorial.  Thank you Jon Lebensold.   \r\n</p>\r\n<p>\r\nPicking up from where we left off in the <a href=\"/stand-alone-zend-form-example\">old post</a> let\'s create a directory called RM to hold our custom element. You may remember that we added the Zend folder\'s parent directory to the include path.  Lets put the RM folder at the same level as the Zend folder so it\'s easily accessible by our code.  We will add the following to our form.php to register our new library namespace:\r\n<pre class=\"brush:php;\">\r\n$autoloader->registerNamespace(  \'RM\'  );\r\n</pre>\r\n</p>\r\n\r\n<p>\r\nWe will also add the following to let ZF know where to find our custom view helpers.  \r\n<pre class=\"brush:php;\">\r\n$view->addHelperPath( __DIR__ . \'/RM/View/Helper\', \'RM_View_Helper\' );\r\n</pre>\r\nThis directory structure is simply following ZF\'s native directory structure.  You can change it around if you want. Note: view helpers are classes that are responsible for producing HTML.  If you don\'t know how view helpers work then you may want to pause and go learn how they work first.  We will write the code for our custom view helper later.  Lets write the code for our custom element\'s control first.\r\n</p>\r\n<p>\r\nWe will create RM/Form/Element directories to hold the control for our custom element.  Again, just following ZF\'s structure.  Lets create a class called Ssn (Ssn.php) inside the Element folder.\r\n<pre class=\"brush:php;\">\r\n&lt;?php\r\n/**\r\n * This class serves as the controller for the custom element Ssn.\r\n * Much of its functionality comes from the parent class \r\n * Zend_Form_Element_Xhtml.\r\n * Since this is a composite element I overrode setValue and getValue methods.\r\n * The value comes in as an array. I convert it into a string because \r\n * Zend validators deal with strings and not arrays.\r\n *\r\n * @author Rayhan Muktader &lt;rmuktader(@)gmail.com&gt;\r\n */\r\nclass RM_Form_Element_Ssn\r\n    extends Zend_Form_Element_Xhtml \r\n{\r\n    /**\r\n     * @var string $helper.\r\n     * The parent class will find the view helper to with this name and use it\r\n     * to render the element.  In this case I created a custom view helper\r\n     * ssnElement.  Custom view helpers must be added:\r\n     * $view-&gt;setHelperPath(\'/path/to/more/helpers\', \'My_View_Helper\');\r\n     * or \r\n     * autoloaderNamespaces[] = &quot;My&quot;\r\n     * resources.view.helperPath.My_View_Helper = &quot;My/View/Helper&quot;\r\n     * with the folder My in the library folder\r\n     */\r\n    public $helper       = \'ssnElement\';\r\n    \r\n    /**\r\n     * @var integer $_areanum\r\n     * Holds the first three digits of a SSN.\r\n     */\r\n    protected $_areanum   = null;\r\n    \r\n    /**\r\n     * @var integer $_groupnum\r\n     * Holds the middle two digits of a SSN.\r\n     */\r\n    protected $_groupnum  = null;\r\n    \r\n    /**\r\n     * @var integer $_serialnum\r\n     * Holds the last four digits of a SSN.\r\n     */\r\n    protected $_serialnum = null;\r\n    \r\n    \r\n    /**\r\n     * @param array $value containing the $value[\'areanum\'], \r\n     * $value[\'groupnum\'] and $value[\'serialnum\'] of a SSN.\r\n     * \r\n     * @return void\r\n     */\r\n    public function setValue( $value )\r\n    { \r\n        if ( is_array( $value )  \r\n            &amp;&amp; isset( $value[\'areanum\'] ) \r\n            &amp;&amp; isset( $value[\'groupnum\'] ) \r\n            &amp;&amp; isset( $value[\'serialnum\'] ) \r\n        ) { \r\n            $this-&gt;setAreanum( $value[\'areanum\'] )\r\n                -&gt;setGroupnum( $value[\'groupnum\'] )\r\n                -&gt;setSerialnum( $value[\'serialnum\'] );\r\n        \r\n        }\r\n        \r\n    }\r\n    \r\n    /**\r\n     * @return string $value, the value set for this element if it is a \r\n     * valid value.\r\n     */\r\n    public function getValue()\r\n    {\r\n        $value = false;\r\n       \r\n        if ( $this-&gt;isValidAreanum($this-&gt;_areanum) &amp;&amp; \r\n            $this-&gt;isValidGroupnum($this-&gt;_groupnum) &amp;&amp; \r\n            $this-&gt;isValidSerialnum($this-&gt;_serialnum) ) {\r\n            \r\n            $value = $this-&gt;_areanum . $this-&gt;_groupnum .  $this-&gt;_serialnum;\r\n        }\r\n        \r\n        return $value;\r\n    }\r\n    \r\n    /**\r\n     * @param string|int $num the first three digits of a SSN.\r\n     * @return object $this.\r\n     */\r\n    public function setAreanum( $num )\r\n    {\r\n        $this-&gt;_areanum = $num;\r\n        return $this;\r\n    }\r\n   \r\n    /**\r\n     * @param string|int $num the middle two digits of a SSN.\r\n     * @return object $this.\r\n     */   \r\n    public function setGroupnum( $num )\r\n    {\r\n        $this-&gt;_groupnum = $num;\r\n        return $this;\r\n    }\r\n    \r\n    /**\r\n     * @param string|int $num the last four digits of a SSN.\r\n     * @return object $this.\r\n     */    \r\n    public function setSerialnum( $num )\r\n    {\r\n        $this-&gt;_serialnum = $num;\r\n        return $this;\r\n    }\r\n    \r\n    /**\r\n     * @param string|int $num the first three digits of a SSN.\r\n     * @return bool TRUE if valid FALSE otherwise.\r\n     */\r\n    public function isValidAreanum( $num )\r\n    {\r\n        $isValid = false;\r\n        \r\n        $intOptions = array(&quot;options&quot;=&gt;\r\n            array(&quot;min_range&quot;=&gt;100, &quot;max_range&quot;=&gt;999));\r\n\r\n        if ( filter_var( $num, FILTER_VALIDATE_INT, $intOptions ) ) {\r\n            $isValid = true; \r\n        }\r\n        \r\n        return $isValid;\r\n    }\r\n    \r\n    /**\r\n     * @param string|int $num the middle two digits of a SSN.\r\n     * @return bool TRUE if valid FALSE otherwise.\r\n     */\r\n    public function isValidGroupnum( $num )\r\n    {\r\n        $isValid = false;\r\n        \r\n        $intOptions = array(&quot;options&quot;=&gt;\r\n            array(&quot;min_range&quot;=&gt;10, &quot;max_range&quot;=&gt;999));\r\n\r\n        if ( filter_var( $num, FILTER_VALIDATE_INT, $intOptions ) ) {\r\n            $isValid = true; \r\n        }\r\n        \r\n        return $isValid;\r\n    }\r\n    \r\n    /**\r\n     * @param string|int $num the last four digits of a SSN.\r\n     * @return bool TRUE if valid FALSE otherwise.\r\n     */\r\n    public function isValidSerialnum( $num )\r\n    {\r\n        $isValid = false;\r\n        \r\n        $intOptions = array(&quot;options&quot;=&gt;\r\n            array(&quot;min_range&quot;=&gt;1000, &quot;max_range&quot;=&gt;9999));\r\n\r\n        if ( filter_var( $num, FILTER_VALIDATE_INT, $intOptions ) ) {\r\n            $isValid = true; \r\n        }\r\n        \r\n        return $isValid;\r\n    }\r\n}\r\n</pre>\r\nThe important thing to note here is that the class extends Zend_Form_Element_Xhtml and lets it do all the heavy lifting.  You need to  specify a public variable called $helper that will specify the name of the view helper class that will be responsible for rendering the HTML.  As I mentioned before, we will build this custom view helper a bit later.  We have to override setValue and getValue methods in our Ssn class because we are dealing with an array from a composite element and ZF only knows how to deal with elements that produce strings as their values. The getVaule and setValue methods are used by ZF every time the element is populated, validated, and data is retrieved.\r\n</p> \r\nLets move onto the view helper that will generate the XHTML for this this element.  So, maintaining ZF\'s structure lets create RM/View/Helper and create a file called SsnElement.php\r\n<pre class=\"brush:php;\">\r\n&lt;?php\r\n/**\r\n * This class serves as the view for the custom element Ssn.\r\n * Much of its functionality comes from the parent class \r\n * Zend_View_Helper_FormElement.\r\n * A method that matches the class name must be present with \r\n * the right method signature.\r\n *\r\n * This element will produce composite element.\r\n *\r\n * @author Rayhan Muktader &lt;rmuktader(@)gmail.com&gt;\r\n */\r\nclass RM_View_Helper_SsnElement\r\n    extends Zend_View_Helper_FormElement\r\n{\r\n     /**\r\n      * @var string $_xhtml. will contain the rendered form element.\r\n      */\r\n    protected $_xhtml = \'\';\r\n    \r\n    /**\r\n     * @param string $name the name of the form element.\r\n     * @param string $value the value of the form element.\r\n     * @param array $arrtibs the attribs for the form element.\r\n     * @return string $_xhtml the rendered xhtml for this form element.\r\n     */\r\n    public function ssnElement( $name, $value = null, $attribs = null )\r\n    { \r\n        // take the incoming SSN string break it up into three pieces for the\r\n        // three elements that will make the composite element.\r\n        $areanum = $groupnum = $serialnum = \'\';\r\n        if ( $value ) { \r\n            $areanum   = substr( $value, 0 , 3 );\r\n            $groupnum  = substr( $value, 3 , 2 );\r\n            $serialnum = substr( $value, 5 , 4 );\r\n        }\r\n        \r\n        // Use Zend_View_Helper_FormText to build the elements that will form\r\n        // the composite element.\r\n        $helper = new Zend_View_Helper_FormText();\r\n        \r\n        // Must assign a view since we are outside zend\'s normal MVC structure.\r\n        $helper-&gt;setView( $this-&gt;view );\r\n\r\n        \r\n        // Create the xhtml\r\n        $this-&gt;_xhtml .= $helper-&gt;formText( \r\n            $name . \'[areanum]\', \r\n            $areanum, \r\n            array( \'size\'=&gt;3, \'maxlength\'=&gt;3 ) \r\n        );\r\n            \r\n        $this-&gt;_xhtml .= $helper-&gt;formText( \r\n            $name . \'[groupnum]\', \r\n            $groupnum, \r\n            array( \'size\'=&gt;2, \'maxlength\'=&gt;2 ) \r\n        );\r\n            \r\n        $this-&gt;_xhtml .= $helper-&gt;formText( \r\n            $name . \'[serialnum]\', \r\n            $serialnum, \r\n            array( \'size\'=&gt;4, \'maxlength\'=&gt;4 ) \r\n        );\r\n        \r\n        return $this-&gt;_xhtml;\r\n    }\r\n}\r\n</pre>\r\nNote that the class extends Zend_View_Helper_FormElement and lets ZF do most of work behind the scene.  In order to take advantage of ZF\'s automated functionality we have to write a method that matches the class\'s name and matches a specific method signature.  \r\n<pre class=\"brush:php;\">\r\n public function ssnElement( $name, $value = null, $attribs = null )\r\n</pre>\r\nZF will pass in the element\'s name defined by you when you instantiate this element, the value retrieved from the getValue method of the element\'s control, and attributes defined by you when you instantiate the element.\r\n</p>\r\n<p>\r\nThen inside the method we use Zend_View_Helper_FormText to create the three text boxes that will hold the three pieces of a SSN and return them as single string.  Since we are working outside of ZF\'s MVC structure we need to specify a view object that can be used by Zend_View_Helper_FormText.\r\n<pre class=\"brush:php;\">\r\n$helper->setView( $this->view );\r\n</pre>\r\n</p>\r\n\r\n<p>\r\nThe final state of form.php looks like this:\r\n<pre class=\"brush:php;\">\r\n&lt;?php\r\n/**\r\n * @file form.php\r\n */\r\n \r\n// Load Zend Framework\r\nset_include_path(\r\n        implode(PATH_SEPARATOR, array(\r\n                get_include_path(),\r\n                __DIR__ .\'/\'\r\n        ))\r\n);\r\n        \r\n// Create the auto loader so zend can load the rest automatically\r\nrequire_once \'Zend/Loader/Autoloader.php\';\r\n$autoloader = Zend_Loader_Autoloader::getInstance();\r\n\r\n// load custom namspace\r\n// in ZF MVC it would be autoloaderNamespaces[] = RM\r\n$autoloader-&gt;registerNamespace( \'RM\' ); \r\n\r\n$form = new Zend_Form;\r\n\r\n// Set form name, id, method and action\r\n$form-&gt;setName( \'contact\' )\r\n    -&gt;setAttrib( \'id\', \'form-contact\' )\r\n    -&gt;setMethod( \'post\' )\r\n    -&gt;setAction( \'http://\'.\r\n        $_SERVER[\'SERVER_NAME\'] . $_SERVER[\'REQUEST_URI\'] \r\n    );\r\n\r\n// Create text box for name\r\n$name = new Zend_Form_Element_Text( \'name\' );\r\n$name-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'fullname\' )\r\n    -&gt;addErrorMessage( \'Please provide your name\' );\r\n\r\n$ssn = new RM_Form_Element_Ssn( \'ssn\' );    \r\n$ssn-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'ssn\' )\r\n    -&gt;addErrorMessage( \'Please provide a valid SSN\' );\r\n    \r\n// Create text box for email        \r\n$email = new Zend_Form_Element_Text( \'email\' );\r\n$email-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'email\' )\r\n    -&gt;addErrorMessage( \'Please provide a valid e-mail address\' );\r\n\r\n// Create text area for message    \r\n$message = new Zend_Form_Element_Textarea( \'message\' );\r\n$message-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'message\' )\r\n    -&gt;addErrorMessage( \'Please specify a message\' );\r\n\r\n// Create a submit button    \r\n$submit = new Zend_Form_Element_Submit( \'submit\' );\r\n$submit-&gt;setLabel( \'Send Your Message!\' )\r\n    -&gt;setAttrib( \'id\', \'submit\' );\r\n\r\n// Set the ViewScript decorator for the form and tell it which \r\n// template file to use\r\n$form-&gt;setDecorators( array( \r\n    array( \'ViewScript\', array( \'viewScript\' =&gt; \'form-contact.tpl.php\' ) ) ) );\r\n\r\n// Add the form elements AFTER the viewscript decorator has been set\r\n$form-&gt;addElements( array( $name, $ssn, $email, $message, $submit ) );\r\n\r\n// Get rid of all element decorators except for ViewHelper to render\r\n// the individual elements and Errors decorator to render the errors.\r\n$form-&gt;setElementDecorators( array( \'ViewHelper\', \'Errors\' ) );\r\n    \r\n// Create an instance of Zend_View and set the directory \r\n// for the template files\r\n$view = new Zend_View();\r\n$view-&gt;setScriptPath( __DIR__ );\r\n$view-&gt;addHelperPath( __DIR__ . \'/RM/View/Helper\', \'RM_View_Helper\' );\r\n\r\n// Tell all the elements in the form which view to use when rendering\r\nforeach ($form as $item){\r\n    $item-&gt;setView($view);\r\n}\r\n\r\n// process or display the form\r\nif( isset( $_POST[\'submit\'] ) &amp;&amp; ( $form-&gt;isValid( $_POST ) ) ) {\r\n    echo \'Thank you\';\r\n} else {\r\n    echo $form-&gt;render( $view );\r\n}\r\n</pre>\r\n</p>\r\n\r\n<p>\r\nThe final state of the template file:\r\n<pre class=\"brush:php;\">\r\n&lt;?php\r\n/**\r\n * @file form-contact.tpl.php\r\n */\r\n?&gt;\r\n&lt;!doctype html&gt;  \r\n&lt;html lang=&quot;en&quot;&gt;  \r\n&lt;head&gt;  \r\n&lt;meta charset=&quot;utf-8&quot;&gt;  \r\n&lt;title&gt;Stand Alone Zend Form&lt;/title&gt;  \r\n&lt;meta name=&quot;description&quot; content=&quot;Stand Alone Zend Form&quot;&gt;  \r\n&lt;meta name=&quot;author&quot; content=&quot;Rayhan Muktader&quot;&gt;  \r\n&lt;!--[if lt IE 9]&gt;  \r\n&lt;script src=&quot;http://html5shiv.googlecode.com/svn/trunk/html5.js&quot;&gt;&lt;/script&gt;  \r\n&lt;![endif]--&gt;  \r\n&lt;/head&gt;  \r\n&lt;body&gt;  \r\n      \r\n    &lt;form id=&quot;contact&quot; action=&quot;&lt;?= $this-&gt;element-&gt;getAction(); ?&gt;&quot; \r\n        method=&quot;&lt;?= $this-&gt;element-&gt;getMethod(); ?&gt;&quot;&gt;\r\n\r\n        &lt;table&gt;\r\n            &lt;tr&gt;\r\n                &lt;td&gt;Your Name&lt;/td&gt;\r\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;name; ?&gt;&lt;/td&gt;\r\n            &lt;/tr&gt;\r\n\r\n            &lt;tr&gt;\r\n                &lt;td&gt;Your E-mail Address&lt;/td&gt;\r\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;email; ?&gt;&lt;/td&gt;\r\n            &lt;/tr&gt;\r\n\r\n            &lt;tr&gt;\r\n                &lt;td&gt;Your SSN&lt;/td&gt;\r\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;ssn; ?&gt;&lt;/td&gt;\r\n            &lt;/tr&gt;\r\n            \r\n            &lt;tr&gt;\r\n                &lt;td&gt;Your Message&lt;/td&gt;\r\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;message; ?&gt;&lt;/td&gt;\r\n            &lt;/tr&gt;\r\n\r\n            &lt;tr&gt;\r\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;submit ?&gt;&lt;/td&gt;\r\n            &lt;/tr&gt;\r\n        &lt;/table&gt;\r\n    &lt;/form&gt;\r\n    \r\n&lt;/body&gt;  \r\n&lt;/html&gt;  \r\n</pre>\r\n</p>\r\n\r\n<p>\r\nHere\'s a <a href=\"/examples/zf-stand-alone-form-with-composite-element/\">demo</a> and here\'s the download <a href=\"https://github.com/rmuktader/zf-stand-alone-form-with-composite-element\">link</a>.\r\n</p>\r\n</p>\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:16112:\"<p>\nI went over how to create <a href=\"/stand-alone-zend-form-example\">stand alone zend forms</a> in a previous post.  This time lets discuss how to build stand alone zend forms with custom composite elements.\n</p>\n<p>\nI had to build a form that has to collect social security numbers.  The client needed a form that would work without javascript and had separate fields for each section of a SSN.  </p>\n<pre class=\"brush:xml;\">\n&lt;input type=\"text\" name=\"ssn[areanum]\" id=\"ssn-areanum\" value=\"\" size=\"3\" maxlength=\"3\"&gt;\n\n&lt;input type=\"text\" name=\"ssn[groupnum]\" id=\"ssn-groupnum\" value=\"\" size=\"2\" maxlength=\"2\"&gt;\n\n&lt;input type=\"text\" name=\"ssn[serialnum]\" id=\"ssn-serialnum\" value=\"\" size=\"4\" maxlength=\"4\"&gt;\n</pre>\n<p>\nTo my pleasant surprise, I found my solution on Jon Lebensold\'s site <a href=\"http://zendcasts.com\">zendcasts.com</a> where there are video tutorials on how to build a composite zend form element that collects phone numbers.  This is where I learned how to do the things I am documenting in this tutorial.  Thank you Jon Lebensold.\n</p>\n<p>\nPicking up from where we left off in the <a href=\"/stand-alone-zend-form-example\">old post</a> let\'s create a directory called RM to hold our custom element. You may remember that we added the Zend folder\'s parent directory to the include path.  Lets put the RM folder at the same level as the Zend folder so it\'s easily accessible by our code.  We will add the following to our form.php to register our new library namespace:</p>\n<pre class=\"brush:php;\">\n$autoloader-&gt;registerNamespace(  \'RM\'  );\n</pre>\n<p>\nWe will also add the following to let ZF know where to find our custom view helpers.  </p>\n<pre class=\"brush:php;\">\n$view-&gt;addHelperPath( __DIR__ . \'/RM/View/Helper\', \'RM_View_Helper\' );\n</pre><p>This directory structure is simply following ZF\'s native directory structure.  You can change it around if you want. Note: view helpers are classes that are responsible for producing HTML.  If you don\'t know how view helpers work then you may want to pause and go learn how they work first.  We will write the code for our custom view helper later.  Lets write the code for our custom element\'s control first.\n</p>\n<p>\nWe will create RM/Form/Element directories to hold the control for our custom element.  Again, just following ZF\'s structure.  Lets create a class called Ssn (Ssn.php) inside the Element folder.</p>\n<pre class=\"brush:php;\">\n&lt;?php\n/**\n * This class serves as the controller for the custom element Ssn.\n * Much of its functionality comes from the parent class \n * Zend_Form_Element_Xhtml.\n * Since this is a composite element I overrode setValue and getValue methods.\n * The value comes in as an array. I convert it into a string because \n * Zend validators deal with strings and not arrays.\n *\n * @author Rayhan Muktader &lt;rmuktader(@)gmail.com&gt;\n */\nclass RM_Form_Element_Ssn\n    extends Zend_Form_Element_Xhtml \n{\n    /**\n     * @var string $helper.\n     * The parent class will find the view helper to with this name and use it\n     * to render the element.  In this case I created a custom view helper\n     * ssnElement.  Custom view helpers must be added:\n     * $view-&gt;setHelperPath(\'/path/to/more/helpers\', \'My_View_Helper\');\n     * or \n     * autoloaderNamespaces[] = \"My\"\n     * resources.view.helperPath.My_View_Helper = \"My/View/Helper\"\n     * with the folder My in the library folder\n     */\n    public $helper       = \'ssnElement\';\n    \n    /**\n     * @var integer $_areanum\n     * Holds the first three digits of a SSN.\n     */\n    protected $_areanum   = null;\n    \n    /**\n     * @var integer $_groupnum\n     * Holds the middle two digits of a SSN.\n     */\n    protected $_groupnum  = null;\n    \n    /**\n     * @var integer $_serialnum\n     * Holds the last four digits of a SSN.\n     */\n    protected $_serialnum = null;\n    \n    \n    /**\n     * @param array $value containing the $value[\'areanum\'], \n     * $value[\'groupnum\'] and $value[\'serialnum\'] of a SSN.\n     * \n     * @return void\n     */\n    public function setValue( $value )\n    { \n        if ( is_array( $value )  \n            &amp;&amp; isset( $value[\'areanum\'] ) \n            &amp;&amp; isset( $value[\'groupnum\'] ) \n            &amp;&amp; isset( $value[\'serialnum\'] ) \n        ) { \n            $this-&gt;setAreanum( $value[\'areanum\'] )\n                -&gt;setGroupnum( $value[\'groupnum\'] )\n                -&gt;setSerialnum( $value[\'serialnum\'] );\n        \n        }\n        \n    }\n    \n    /**\n     * @return string $value, the value set for this element if it is a \n     * valid value.\n     */\n    public function getValue()\n    {\n        $value = false;\n       \n        if ( $this-&gt;isValidAreanum($this-&gt;_areanum) &amp;&amp; \n            $this-&gt;isValidGroupnum($this-&gt;_groupnum) &amp;&amp; \n            $this-&gt;isValidSerialnum($this-&gt;_serialnum) ) {\n            \n            $value = $this-&gt;_areanum . $this-&gt;_groupnum .  $this-&gt;_serialnum;\n        }\n        \n        return $value;\n    }\n    \n    /**\n     * @param string|int $num the first three digits of a SSN.\n     * @return object $this.\n     */\n    public function setAreanum( $num )\n    {\n        $this-&gt;_areanum = $num;\n        return $this;\n    }\n   \n    /**\n     * @param string|int $num the middle two digits of a SSN.\n     * @return object $this.\n     */   \n    public function setGroupnum( $num )\n    {\n        $this-&gt;_groupnum = $num;\n        return $this;\n    }\n    \n    /**\n     * @param string|int $num the last four digits of a SSN.\n     * @return object $this.\n     */    \n    public function setSerialnum( $num )\n    {\n        $this-&gt;_serialnum = $num;\n        return $this;\n    }\n    \n    /**\n     * @param string|int $num the first three digits of a SSN.\n     * @return bool TRUE if valid FALSE otherwise.\n     */\n    public function isValidAreanum( $num )\n    {\n        $isValid = false;\n        \n        $intOptions = array(\"options\"=&gt;\n            array(\"min_range\"=&gt;100, \"max_range\"=&gt;999));\n\n        if ( filter_var( $num, FILTER_VALIDATE_INT, $intOptions ) ) {\n            $isValid = true; \n        }\n        \n        return $isValid;\n    }\n    \n    /**\n     * @param string|int $num the middle two digits of a SSN.\n     * @return bool TRUE if valid FALSE otherwise.\n     */\n    public function isValidGroupnum( $num )\n    {\n        $isValid = false;\n        \n        $intOptions = array(\"options\"=&gt;\n            array(\"min_range\"=&gt;10, \"max_range\"=&gt;999));\n\n        if ( filter_var( $num, FILTER_VALIDATE_INT, $intOptions ) ) {\n            $isValid = true; \n        }\n        \n        return $isValid;\n    }\n    \n    /**\n     * @param string|int $num the last four digits of a SSN.\n     * @return bool TRUE if valid FALSE otherwise.\n     */\n    public function isValidSerialnum( $num )\n    {\n        $isValid = false;\n        \n        $intOptions = array(\"options\"=&gt;\n            array(\"min_range\"=&gt;1000, \"max_range\"=&gt;9999));\n\n        if ( filter_var( $num, FILTER_VALIDATE_INT, $intOptions ) ) {\n            $isValid = true; \n        }\n        \n        return $isValid;\n    }\n}\n</pre><p>The important thing to note here is that the class extends Zend_Form_Element_Xhtml and lets it do all the heavy lifting.  You need to  specify a public variable called $helper that will specify the name of the view helper class that will be responsible for rendering the HTML.  As I mentioned before, we will build this custom view helper a bit later.  We have to override setValue and getValue methods in our Ssn class because we are dealing with an array from a composite element and ZF only knows how to deal with elements that produce strings as their values. The getVaule and setValue methods are used by ZF every time the element is populated, validated, and data is retrieved.\n</p>\n<p>Lets move onto the view helper that will generate the XHTML for this this element.  So, maintaining ZF\'s structure lets create RM/View/Helper and create a file called SsnElement.php</p>\n<pre class=\"brush:php;\">\n&lt;?php\n/**\n * This class serves as the view for the custom element Ssn.\n * Much of its functionality comes from the parent class \n * Zend_View_Helper_FormElement.\n * A method that matches the class name must be present with \n * the right method signature.\n *\n * This element will produce composite element.\n *\n * @author Rayhan Muktader &lt;rmuktader(@)gmail.com&gt;\n */\nclass RM_View_Helper_SsnElement\n    extends Zend_View_Helper_FormElement\n{\n     /**\n      * @var string $_xhtml. will contain the rendered form element.\n      */\n    protected $_xhtml = \'\';\n    \n    /**\n     * @param string $name the name of the form element.\n     * @param string $value the value of the form element.\n     * @param array $arrtibs the attribs for the form element.\n     * @return string $_xhtml the rendered xhtml for this form element.\n     */\n    public function ssnElement( $name, $value = null, $attribs = null )\n    { \n        // take the incoming SSN string break it up into three pieces for the\n        // three elements that will make the composite element.\n        $areanum = $groupnum = $serialnum = \'\';\n        if ( $value ) { \n            $areanum   = substr( $value, 0 , 3 );\n            $groupnum  = substr( $value, 3 , 2 );\n            $serialnum = substr( $value, 5 , 4 );\n        }\n        \n        // Use Zend_View_Helper_FormText to build the elements that will form\n        // the composite element.\n        $helper = new Zend_View_Helper_FormText();\n        \n        // Must assign a view since we are outside zend\'s normal MVC structure.\n        $helper-&gt;setView( $this-&gt;view );\n\n        \n        // Create the xhtml\n        $this-&gt;_xhtml .= $helper-&gt;formText( \n            $name . \'[areanum]\', \n            $areanum, \n            array( \'size\'=&gt;3, \'maxlength\'=&gt;3 ) \n        );\n            \n        $this-&gt;_xhtml .= $helper-&gt;formText( \n            $name . \'[groupnum]\', \n            $groupnum, \n            array( \'size\'=&gt;2, \'maxlength\'=&gt;2 ) \n        );\n            \n        $this-&gt;_xhtml .= $helper-&gt;formText( \n            $name . \'[serialnum]\', \n            $serialnum, \n            array( \'size\'=&gt;4, \'maxlength\'=&gt;4 ) \n        );\n        \n        return $this-&gt;_xhtml;\n    }\n}\n</pre><p>Note that the class extends Zend_View_Helper_FormElement and lets ZF do most of work behind the scene.  In order to take advantage of ZF\'s automated functionality we have to write a method that matches the class\'s name and matches a specific method signature.  </p>\n<pre class=\"brush:php;\">\n public function ssnElement( $name, $value = null, $attribs = null )\n</pre><p>ZF will pass in the element\'s name defined by you when you instantiate this element, the value retrieved from the getValue method of the element\'s control, and attributes defined by you when you instantiate the element.\n</p>\n<p>\nThen inside the method we use Zend_View_Helper_FormText to create the three text boxes that will hold the three pieces of a SSN and return them as single string.  Since we are working outside of ZF\'s MVC structure we need to specify a view object that can be used by Zend_View_Helper_FormText.</p>\n<pre class=\"brush:php;\">\n$helper-&gt;setView( $this-&gt;view );\n</pre>\n<p>\nThe final state of form.php looks like this:</p>\n<pre class=\"brush:php;\">\n&lt;?php\n/**\n * @file form.php\n */\n \n// Load Zend Framework\nset_include_path(\n        implode(PATH_SEPARATOR, array(\n                get_include_path(),\n                __DIR__ .\'/\'\n        ))\n);\n        \n// Create the auto loader so zend can load the rest automatically\nrequire_once \'Zend/Loader/Autoloader.php\';\n$autoloader = Zend_Loader_Autoloader::getInstance();\n\n// load custom namspace\n// in ZF MVC it would be autoloaderNamespaces[] = RM\n$autoloader-&gt;registerNamespace( \'RM\' ); \n\n$form = new Zend_Form;\n\n// Set form name, id, method and action\n$form-&gt;setName( \'contact\' )\n    -&gt;setAttrib( \'id\', \'form-contact\' )\n    -&gt;setMethod( \'post\' )\n    -&gt;setAction( \'http://\'.\n        $_SERVER[\'SERVER_NAME\'] . $_SERVER[\'REQUEST_URI\'] \n    );\n\n// Create text box for name\n$name = new Zend_Form_Element_Text( \'name\' );\n$name-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'fullname\' )\n    -&gt;addErrorMessage( \'Please provide your name\' );\n\n$ssn = new RM_Form_Element_Ssn( \'ssn\' );    \n$ssn-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'ssn\' )\n    -&gt;addErrorMessage( \'Please provide a valid SSN\' );\n    \n// Create text box for email        \n$email = new Zend_Form_Element_Text( \'email\' );\n$email-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'email\' )\n    -&gt;addErrorMessage( \'Please provide a valid e-mail address\' );\n\n// Create text area for message    \n$message = new Zend_Form_Element_Textarea( \'message\' );\n$message-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'message\' )\n    -&gt;addErrorMessage( \'Please specify a message\' );\n\n// Create a submit button    \n$submit = new Zend_Form_Element_Submit( \'submit\' );\n$submit-&gt;setLabel( \'Send Your Message!\' )\n    -&gt;setAttrib( \'id\', \'submit\' );\n\n// Set the ViewScript decorator for the form and tell it which \n// template file to use\n$form-&gt;setDecorators( array( \n    array( \'ViewScript\', array( \'viewScript\' =&gt; \'form-contact.tpl.php\' ) ) ) );\n\n// Add the form elements AFTER the viewscript decorator has been set\n$form-&gt;addElements( array( $name, $ssn, $email, $message, $submit ) );\n\n// Get rid of all element decorators except for ViewHelper to render\n// the individual elements and Errors decorator to render the errors.\n$form-&gt;setElementDecorators( array( \'ViewHelper\', \'Errors\' ) );\n    \n// Create an instance of Zend_View and set the directory \n// for the template files\n$view = new Zend_View();\n$view-&gt;setScriptPath( __DIR__ );\n$view-&gt;addHelperPath( __DIR__ . \'/RM/View/Helper\', \'RM_View_Helper\' );\n\n// Tell all the elements in the form which view to use when rendering\nforeach ($form as $item){\n    $item-&gt;setView($view);\n}\n\n// process or display the form\nif( isset( $_POST[\'submit\'] ) &amp;&amp; ( $form-&gt;isValid( $_POST ) ) ) {\n    echo \'Thank you\';\n} else {\n    echo $form-&gt;render( $view );\n}\n</pre>\n<p>\nThe final state of the template file:</p>\n<pre class=\"brush:php;\">\n&lt;?php\n/**\n * @file form-contact.tpl.php\n */\n?&gt;\n&lt;!doctype html&gt;  \n&lt;html lang=\"en\"&gt;  \n&lt;head&gt;  \n&lt;meta charset=\"utf-8\"&gt;  \n&lt;title&gt;Stand Alone Zend Form&lt;/title&gt;  \n&lt;meta name=\"description\" content=\"Stand Alone Zend Form\"&gt;  \n&lt;meta name=\"author\" content=\"Rayhan Muktader\"&gt;  \n&lt;!--[if lt IE 9]&gt;  \n&lt;script src=\"<a href=\"http://html5shiv.googlecode.com/svn/trunk/html5.js&quot;&gt;&lt;/script&gt;\">http://html5shiv.googlecode.com/svn/trunk/html5.js\"&gt;&lt;/script&gt;</a>  \n&lt;![endif]--&gt;  \n&lt;/head&gt;  \n&lt;body&gt;  \n      \n    &lt;form id=\"contact\" action=\"&lt;?= $this-&gt;element-&gt;getAction(); ?&gt;\" \n        method=\"&lt;?= $this-&gt;element-&gt;getMethod(); ?&gt;\"&gt;\n\n        &lt;table&gt;\n            &lt;tr&gt;\n                &lt;td&gt;Your Name&lt;/td&gt;\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;name; ?&gt;&lt;/td&gt;\n            &lt;/tr&gt;\n\n            &lt;tr&gt;\n                &lt;td&gt;Your E-mail Address&lt;/td&gt;\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;email; ?&gt;&lt;/td&gt;\n            &lt;/tr&gt;\n\n            &lt;tr&gt;\n                &lt;td&gt;Your SSN&lt;/td&gt;\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;ssn; ?&gt;&lt;/td&gt;\n            &lt;/tr&gt;\n            \n            &lt;tr&gt;\n                &lt;td&gt;Your Message&lt;/td&gt;\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;message; ?&gt;&lt;/td&gt;\n            &lt;/tr&gt;\n\n            &lt;tr&gt;\n                &lt;td&gt;&lt;?= $this-&gt;element-&gt;submit ?&gt;&lt;/td&gt;\n            &lt;/tr&gt;\n        &lt;/table&gt;\n    &lt;/form&gt;\n    \n&lt;/body&gt;  \n&lt;/html&gt;  \n</pre>\n<p>\nHere\'s a <a href=\"/examples/zf-stand-alone-form-with-composite-element/\">demo</a> and here\'s the download <a href=\"https://github.com/rmuktader/zf-stand-alone-form-with-composite-element\">link</a>.\n</p>\n\n\";s:12:\"safe_summary\";s:0:\"\";}}}s:10:\"field_tags\";a:1:{s:3:\"und\";a:4:{i:0;a:1:{s:3:\"tid\";s:1:\"3\";}i:1;a:1:{s:3:\"tid\";s:1:\"4\";}i:2;a:1:{s:3:\"tid\";s:1:\"7\";}i:3;a:1:{s:3:\"tid\";s:1:\"8\";}}}s:11:\"field_image\";a:0:{}}',0,1348246447,1),('field:node:7','a:3:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:6747:\"<p>\r\nIn this tutorial we will continue on from the <a href=\"/stand-alone-zend-form-composite-element\">last post</a> and learn how build a custom validation for form elements.\r\n</p>\r\n\r\n<p>\r\nThe rule of thumb is that you create a folder called \"Validate\" inside your custom library folder(the RM folder in our case) and inside that folder you create a class that extends Zend_Validate_Abstract and has a isValid($value) method that checks for your custom requirements and return either true or false.   \r\n</p>\r\n\r\n<p>\r\nIt is also important to note that you can take advantage of ZF\'s error message system by creating a protected array called $_messageTemplates and saving the error messages in it.  Then you simply have to call $this->_error( YOUR_ERROR_KEY ) and pass in the key for the error message you want. \r\n</p>\r\n\r\n<p>\r\nIn this silly experiment I create a validator that checks to make sure that there is at least one \'5\' in the SSN.  You can create a validator check for any rule you please and you can check for multiple rules and have different error messages for different rules.\r\n</p>\r\n\r\n<p>\r\nThen you simply add the validator to any form element like so  $element->addValidator(new RM_Validate_SpecialSsn);\r\n</p>\r\n\r\n<p>\r\nHere\'s the code the validator class:\r\n<pre class=\"brush:php;\">\r\n&lt;?php\r\n/**\r\n * This validation class makes sure that there\'s at least one 5 in the value.\r\n * Much of its functionality comes from the parent class \r\n * Zend_Validate_Abstract.\r\n * This is just a silly example. It can be changed to check for more than one\r\n * error and used with different form elements.\r\n *\r\n * @author Rayhan Muktader &lt;rmuktader(@)gmail.com&gt;\r\n */\r\nclass RM_Validate_SpecialSsn\r\n    extends Zend_Validate_Abstract\r\n{\r\n    const INVALID_SPECIAL_SSN = \'invalidSpecialSsn\';\r\n    \r\n    /**\r\n     * @var array $_messageTemplates holds all the names of the errors \r\n     * and the corresponding error messages\r\n     */\r\n    protected $_messageTemplates = array(\r\n        self::INVALID_SPECIAL_SSN =&gt; &quot;Sorry we are only accepting spcial \r\n        social security numbers that have at least one 5 in them. %value% \r\n        is not a special social security number&quot;\r\n    );\r\n    \r\n    \r\n    /**\r\n     * @param string $value the value of the form element\r\n     * @return bool return true if it is a &quot;special&quot; SSN, false otherwise\r\n     */\r\n    public function isValid( $value )\r\n    {\r\n        $isValid = FALSE;\r\n        \r\n        // chech if  the value contains a 5\r\n        if ( strpos( $value, \'5\' ) !== FALSE ) {\r\n            $isValid = TRUE;\r\n        } else {\r\n        \r\n            // sets the value in the form element and makes it available to\r\n            // be printed out in the error message\r\n            $this-&gt;_setValue( $value );\r\n            \r\n            $this-&gt;_error( self::INVALID_SPECIAL_SSN );\r\n        }\r\n        \r\n        return $isValid;\r\n    }\r\n}\r\n</pre>\r\n</p>\r\n<p>\r\nHere the altered code for form.php:\r\n<pre class=\"brush:php;\">\r\n&lt;?php\r\nerror_reporting(E_ALL);\r\nini_set(\'display_errors\', \'1\');\r\n/**\r\n * @file form.php\r\n */\r\n \r\n// Load Zend Framework\r\nset_include_path(\r\n        implode(PATH_SEPARATOR, array(\r\n                get_include_path(),\r\n                __DIR__ .\'/\'\r\n        ))\r\n);\r\n        \r\n// Create the auto loader so zend can load the rest automatically\r\nrequire_once \'Zend/Loader/Autoloader.php\';\r\n$autoloader = Zend_Loader_Autoloader::getInstance();\r\n\r\n// load custom namspace\r\n// in ZF MVC it would be autoloaderNamespaces[] = RM\r\n$autoloader-&gt;registerNamespace( \'RM\' ); \r\n\r\n$form = new Zend_Form;\r\n\r\n// Set form name, id, method and action\r\n$form-&gt;setName( \'contact\' )\r\n    -&gt;setAttrib( \'id\', \'form-contact\' )\r\n    -&gt;setMethod( \'post\' )\r\n    -&gt;setAction( \'http://\'.\r\n        $_SERVER[\'SERVER_NAME\'] . $_SERVER[\'REQUEST_URI\'] \r\n    );\r\n\r\n// Create text box for name\r\n$name = new Zend_Form_Element_Text( \'name\' );\r\n$name-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'fullname\' )\r\n    -&gt;addErrorMessage( \'Please provide your name\' );\r\n\r\n// Create a custom composite element for SSn\r\n$ssn = new RM_Form_Element_Ssn( \'ssn\' );    \r\n$ssn-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'ssn\' )\r\n    -&gt;addValidator( new Zend_Validate_NotEmpty )\r\n    -&gt;addValidator( new RM_Validate_SpecialSsn );\r\n    \r\n// Using addErrorMessage orvrrides all error messages, including the one\r\n// returned by RM_Validate_SpecialSsn.  \r\n// The custom element does not set the values if it is not a valid SSN. \r\n// In those cases, it actually failes the NotEmpty validation.\r\n// I am setting a error message specificly for those cases. This way there\r\n// is no conflict with other other validation messages\r\n    $ssn-&gt;getValidator(\'NotEmpty\')-&gt;setMessage( \'Please provide a valid SSN\' );\r\n\r\n    \r\n// Create text box for email        \r\n$email = new Zend_Form_Element_Text( \'email\' );\r\n$email-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'email\' )\r\n    -&gt;addErrorMessage( \'Please provide a valid e-mail address\' );\r\n\r\n// Create text area for message    \r\n$message = new Zend_Form_Element_Textarea( \'message\' );\r\n$message-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'message\' )\r\n    -&gt;addErrorMessage( \'Please specify a message\' );\r\n\r\n// Create a submit button    \r\n$submit = new Zend_Form_Element_Submit( \'submit\' );\r\n$submit-&gt;setLabel( \'Send Your Message!\' )\r\n    -&gt;setAttrib( \'id\', \'submit\' );\r\n\r\n// Set the ViewScript decorator for the form and tell it which \r\n// template file to use\r\n$form-&gt;setDecorators( array( \r\n    array( \'ViewScript\', array( \'viewScript\' =&gt; \'form-contact.tpl.php\' ) ) ) );\r\n\r\n// Add the form elements AFTER the viewscript decorator has been set\r\n$form-&gt;addElements( array( $name, $ssn, $email, $message, $submit ) );\r\n\r\n// Get rid of all element decorators except for ViewHelper to render\r\n// the individual elements and Errors decorator to render the errors.\r\n$form-&gt;setElementDecorators( array( \'ViewHelper\', \'Errors\' ) );\r\n    \r\n// Create an instance of Zend_View and set the directory \r\n// for the template files\r\n$view = new Zend_View();\r\n$view-&gt;setScriptPath( __DIR__ );\r\n$view-&gt;addHelperPath( __DIR__ . \'/RM/View/Helper\', \'RM_View_Helper\' );\r\n\r\n// Tell all the elements in the form which view to use when rendering\r\nforeach ($form as $item){\r\n    $item-&gt;setView($view);\r\n}\r\n\r\n// process or display the form\r\nif( isset( $_POST[\'submit\'] ) &amp;&amp; ( $form-&gt;isValid( $_POST ) ) ) {\r\n    echo \'Thank you\';\r\n} else {\r\n    echo $form-&gt;render( $view );\r\n}\r\n</pre>\r\n</p>\r\n\r\n<p>\r\nHere\'s the <a href=\"/examples/zf-stand-alone-form-with-composite-element-and-custom-validator/\">demo</a> and <a href=\"https://github.com/rmuktader/zf-custom-validation\">download</a>.\r\n</p>\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:6539:\"<p>\nIn this tutorial we will continue on from the <a href=\"/stand-alone-zend-form-composite-element\">last post</a> and learn how build a custom validation for form elements.\n</p>\n<p>\nThe rule of thumb is that you create a folder called \"Validate\" inside your custom library folder(the RM folder in our case) and inside that folder you create a class that extends Zend_Validate_Abstract and has a isValid($value) method that checks for your custom requirements and return either true or false.\n</p>\n<p>\nIt is also important to note that you can take advantage of ZF\'s error message system by creating a protected array called $_messageTemplates and saving the error messages in it.  Then you simply have to call $this-&gt;_error( YOUR_ERROR_KEY ) and pass in the key for the error message you want.\n</p>\n<p>\nIn this silly experiment I create a validator that checks to make sure that there is at least one \'5\' in the SSN.  You can create a validator check for any rule you please and you can check for multiple rules and have different error messages for different rules.\n</p>\n<p>\nThen you simply add the validator to any form element like so  $element-&gt;addValidator(new RM_Validate_SpecialSsn);\n</p>\n<p>\nHere\'s the code the validator class:</p>\n<pre class=\"brush:php;\">\n&lt;?php\n/**\n * This validation class makes sure that there\'s at least one 5 in the value.\n * Much of its functionality comes from the parent class \n * Zend_Validate_Abstract.\n * This is just a silly example. It can be changed to check for more than one\n * error and used with different form elements.\n *\n * @author Rayhan Muktader &lt;rmuktader(@)gmail.com&gt;\n */\nclass RM_Validate_SpecialSsn\n    extends Zend_Validate_Abstract\n{\n    const INVALID_SPECIAL_SSN = \'invalidSpecialSsn\';\n    \n    /**\n     * @var array $_messageTemplates holds all the names of the errors \n     * and the corresponding error messages\n     */\n    protected $_messageTemplates = array(\n        self::INVALID_SPECIAL_SSN =&gt; \"Sorry we are only accepting spcial \n        social security numbers that have at least one 5 in them. %value% \n        is not a special social security number\"\n    );\n    \n    \n    /**\n     * @param string $value the value of the form element\n     * @return bool return true if it is a \"special\" SSN, false otherwise\n     */\n    public function isValid( $value )\n    {\n        $isValid = FALSE;\n        \n        // chech if  the value contains a 5\n        if ( strpos( $value, \'5\' ) !== FALSE ) {\n            $isValid = TRUE;\n        } else {\n        \n            // sets the value in the form element and makes it available to\n            // be printed out in the error message\n            $this-&gt;_setValue( $value );\n            \n            $this-&gt;_error( self::INVALID_SPECIAL_SSN );\n        }\n        \n        return $isValid;\n    }\n}\n</pre>\n<p>\nHere the altered code for form.php:</p>\n<pre class=\"brush:php;\">\n&lt;?php\nerror_reporting(E_ALL);\nini_set(\'display_errors\', \'1\');\n/**\n * @file form.php\n */\n \n// Load Zend Framework\nset_include_path(\n        implode(PATH_SEPARATOR, array(\n                get_include_path(),\n                __DIR__ .\'/\'\n        ))\n);\n        \n// Create the auto loader so zend can load the rest automatically\nrequire_once \'Zend/Loader/Autoloader.php\';\n$autoloader = Zend_Loader_Autoloader::getInstance();\n\n// load custom namspace\n// in ZF MVC it would be autoloaderNamespaces[] = RM\n$autoloader-&gt;registerNamespace( \'RM\' ); \n\n$form = new Zend_Form;\n\n// Set form name, id, method and action\n$form-&gt;setName( \'contact\' )\n    -&gt;setAttrib( \'id\', \'form-contact\' )\n    -&gt;setMethod( \'post\' )\n    -&gt;setAction( \'http://\'.\n        $_SERVER[\'SERVER_NAME\'] . $_SERVER[\'REQUEST_URI\'] \n    );\n\n// Create text box for name\n$name = new Zend_Form_Element_Text( \'name\' );\n$name-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'fullname\' )\n    -&gt;addErrorMessage( \'Please provide your name\' );\n\n// Create a custom composite element for SSn\n$ssn = new RM_Form_Element_Ssn( \'ssn\' );    \n$ssn-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'ssn\' )\n    -&gt;addValidator( new Zend_Validate_NotEmpty )\n    -&gt;addValidator( new RM_Validate_SpecialSsn );\n    \n// Using addErrorMessage orvrrides all error messages, including the one\n// returned by RM_Validate_SpecialSsn.  \n// The custom element does not set the values if it is not a valid SSN. \n// In those cases, it actually failes the NotEmpty validation.\n// I am setting a error message specificly for those cases. This way there\n// is no conflict with other other validation messages\n    $ssn-&gt;getValidator(\'NotEmpty\')-&gt;setMessage( \'Please provide a valid SSN\' );\n\n    \n// Create text box for email        \n$email = new Zend_Form_Element_Text( \'email\' );\n$email-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'email\' )\n    -&gt;addErrorMessage( \'Please provide a valid e-mail address\' );\n\n// Create text area for message    \n$message = new Zend_Form_Element_Textarea( \'message\' );\n$message-&gt;setRequired( TRUE )\n    -&gt;setAttrib( \'id\', \'message\' )\n    -&gt;addErrorMessage( \'Please specify a message\' );\n\n// Create a submit button    \n$submit = new Zend_Form_Element_Submit( \'submit\' );\n$submit-&gt;setLabel( \'Send Your Message!\' )\n    -&gt;setAttrib( \'id\', \'submit\' );\n\n// Set the ViewScript decorator for the form and tell it which \n// template file to use\n$form-&gt;setDecorators( array( \n    array( \'ViewScript\', array( \'viewScript\' =&gt; \'form-contact.tpl.php\' ) ) ) );\n\n// Add the form elements AFTER the viewscript decorator has been set\n$form-&gt;addElements( array( $name, $ssn, $email, $message, $submit ) );\n\n// Get rid of all element decorators except for ViewHelper to render\n// the individual elements and Errors decorator to render the errors.\n$form-&gt;setElementDecorators( array( \'ViewHelper\', \'Errors\' ) );\n    \n// Create an instance of Zend_View and set the directory \n// for the template files\n$view = new Zend_View();\n$view-&gt;setScriptPath( __DIR__ );\n$view-&gt;addHelperPath( __DIR__ . \'/RM/View/Helper\', \'RM_View_Helper\' );\n\n// Tell all the elements in the form which view to use when rendering\nforeach ($form as $item){\n    $item-&gt;setView($view);\n}\n\n// process or display the form\nif( isset( $_POST[\'submit\'] ) &amp;&amp; ( $form-&gt;isValid( $_POST ) ) ) {\n    echo \'Thank you\';\n} else {\n    echo $form-&gt;render( $view );\n}\n</pre>\n<p>\nHere\'s the <a href=\"/examples/zf-stand-alone-form-with-composite-element-and-custom-validator/\">demo</a> and <a href=\"https://github.com/rmuktader/zf-custom-validation\">download</a>.\n</p>\n\";s:12:\"safe_summary\";s:0:\"\";}}}s:10:\"field_tags\";a:1:{s:3:\"und\";a:3:{i:0;a:1:{s:3:\"tid\";s:1:\"3\";}i:1;a:1:{s:3:\"tid\";s:1:\"4\";}i:2;a:1:{s:3:\"tid\";s:1:\"9\";}}}s:11:\"field_image\";a:0:{}}',0,1348246447,1),('field:node:8','a:3:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:5946:\"<p>\r\nIn this post I\'ll document how to build a stand alone zend framework upload form.\r\n</p>\r\n\r\n<p>\r\nI am using the slightly modified version the form code I had used for my previous posts.  This time I added a Zend_Form_element_File and used the receive() function on it to save the file.\r\n</p>\r\n\r\n<p><a href=\"https://github.com/rmuktader/zf-stand-alone-upload-form\">Download link</a></p>\r\n\r\n<pre class=\"brush:php;\">\r\n&lt;?php\r\nerror_reporting(E_ALL);\r\nini_set(\'display_errors\', \'1\');\r\n/**\r\n * @file form.php\r\n */\r\n \r\n// Load Zend Framework\r\nset_include_path(\r\n        implode(PATH_SEPARATOR, array(\r\n                get_include_path(),\r\n                __DIR__ .\'/\'\r\n        ))\r\n);\r\n        \r\n// Create the auto loader so zend can load the rest automatically\r\nrequire_once \'Zend/Loader/Autoloader.php\';\r\n$autoloader = Zend_Loader_Autoloader::getInstance();\r\n\r\n$form = new Zend_Form;\r\n\r\n// Set form name, id, method and action\r\n$form-&gt;setName( \'fileUpload\' )\r\n    -&gt;setAttrib( \'id\', \'fileUpload\' )\r\n    -&gt;setMethod( \'post\' )\r\n    -&gt;setAttrib(\'enctype\', \'multipart/form-data\')\r\n    -&gt;setAction( \'http://\'.\r\n        $_SERVER[\'SERVER_NAME\'] . $_SERVER[\'REQUEST_URI\'] \r\n    );\r\n    \r\n$file = new Zend_Form_Element_File( \'file\' );\r\n        $file-&gt;setLabel( \'File\' )\r\n            -&gt;setRequired(true)\r\n            //do not save file when getValues() is called\r\n            -&gt;setValueDisabled(False)\r\n            -&gt;addValidator( \'Extension\', FALSE, \'jpg,jpeg\' )\r\n            -&gt;addValidator(\r\n                \'Size\', \r\n                false, \r\n                array( \'min\' =&gt; 20, \'max\' =&gt; 20000000 ) // 2MB\r\n            );\r\n             \r\n$file-&gt;getValidator(\'Extension\')-&gt;setMessage( \'JPEGs only\' );             \r\n            \r\n// Create text area for message    \r\n$message = new Zend_Form_Element_Textarea( \'message\' );\r\n$message-&gt;setRequired( TRUE )\r\n    -&gt;setAttrib( \'id\', \'message\' )\r\n    -&gt;addErrorMessage( \'Please specify a message\' );\r\n\r\n// Create a submit button    \r\n$submit = new Zend_Form_Element_Submit( \'submit\' );\r\n$submit-&gt;setLabel( \'Submit\' )\r\n    -&gt;setAttrib( \'id\', \'submit\' );\r\n\r\n// Set the ViewScript decorator for the form and tell it which \r\n// template file to use\r\n$form-&gt;setDecorators( array( \r\n    array( \'ViewScript\', array( \'viewScript\' =&gt; \'form-contact.tpl.php\' ) ) ) );\r\n\r\n// Add the form elements AFTER the viewscript decorator has been set\r\n$form-&gt;addElements( array( $